import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { ProductConsumer } from '../context'

export default class Product extends Component {
  render() {
    const {id, title, img, price, inCart} = this.props.product;
    return (
      <div className='col-9'>
      <div className='card'>
      <div className='img-container p-5' onClick={()=>console.log('you clickin me on container')}>
      <Link to="/details">
      <img src = {img} alt="product-img" className='card-img-top'/>
      </Link>

      <button className='cart-btn' disabled={inCart?true:false} onClick={()=>console.log('added to cart')}>
      {inCart?<p className='text-capitalize mb-0' disabled>in cart</p>:(<i className='fa fa-cart-plus'></i>)}
      </button>
      </div>
      </div>
      </div>
    )
  }
}
